graphql.query:
{
    user {
        id,
        firstName,
        lastName,
        email
    }
    memberType {
        id,
        discount
    },
    profile {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
        memberTypeId,
        userId
    },
    post {
        id,
        userId,
        title,
        content
    }
}

{
    user(id: "Enter a real id of user") {
        id,
        firstName,
        lastName,
        email
    }
    memberType(id: "Enter a real id of member type") {
        id,
        discount
    },
    profile(id: "Enter a real id of profile") {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
        memberTypeId,
        userId
    },
    post(id: "Enter a real id of post") {
        id,
        userId,
        title,
        content
    }
}

mutation ($user: userInput) {
    createUser(user: $user) {
        id,
        firstName,
        lastName,
        email
    }
}

mutation ($profile: profileInput) {
    createProfile(profile: $profile) {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
        memberTypeId,
        userId
    }
}

mutation ($post: postInput) {
    createPost(post: $post) {
        id,
        userId,
        title,
        content
    }
}
