graphql.query:
{
    user {
        id,
        firstName,
        lastName,
        email,
        subscribedToUserIds
    },
    profile {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
        memberTypeId,
        userId
    },
    post {
        id,
        userId,
        title,
        content
    },
    memberType {
        id,
        discount,
        monthPostsLimit
    }
}

query($memberTypeId: memberTypeEnum, $userID: ID, $profileId: ID, $postId: ID) {
    user(id: $userID) {
        id,
        firstName,
        lastName,
        email
    }
    memberType(id: $memberTypeId) {
        id,
        discount
    },
    profile(id: $profileId) {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
        memberTypeId,
        userId
    },
    post(id: $postId) {
        id,
        userId,
        title,
        content
    }
}

