mutation ($user: userInput) {
    createUser(user: $user) {
        id,
        firstName,
        lastName,
        email,
        subscribedToUserIds
    }
}

mutation ($profile: profileInput) {
    createProfile(profile: $profile) {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
        memberTypeId,
        userId
    }
}

mutation ($post: postInput) {
    createPost(post: $post) {
        id,
        userId,
        title,
        content
    }
}

mutation ($user: userUpdateInput, $id: ID) {
    updateUser(user: $user, id: $id) {
        id,
        firstName,
        lastName,
        email,
        subscribedToUserIds
    }
}

mutation ($profile: profileUpdateInput, $id: ID) {
    updateProfile(profile: $profile, id: $id) {
        id,
        avatar,
        sex,
        birthday,
        country,
        street,
        city,
