        content
    }
}

mutation ($memberTypeData: memberInputUpdateType, $id: memberTypeEnum) {
    updateMemberType(memberTypeData: $memberTypeData, id: $id) {
        id,
        discount,
        monthPostsLimit
    }
}

mutation($currentUserId: ID, $targetUserId: ID) {
    subscribeToUser(currentUserId: $currentUserId, targetUserId: $targetUserId) {
        id,
        firstName,
        lastName,
        email,
        subscribedToUserIds
    }
}

mutation($currentUserId: ID, $targetUserId: ID) {
    unsubscribeFromUser(currentUserId: $currentUserId, targetUserId: $targetUserId) {
        id,
        firstName,
        lastName,
        email,
        subscribedToUserIds
    }
}
